import React from 'react';
import PropTypes from 'prop-types';

import './Button.css';
// import './Button.scss';

/**
Guardian Mint buttons have the highest level of emphasis of action on a page. They are limited to a single instance per page and use large text over Guardian Mint.
*/

export default function PrimaryButton({ color, textDecoration, backgroundColor, id, size, onClick, disabled, children }) {
	const styles = {
		backgroundColor,
		color,
		textDecoration
	};

	return (
		<button className="primaryButton" id={id} style={styles} onClick={onClick} disabled={disabled}>
			{children}
		</button>
	);
}

PrimaryButton.propTypes = {
	/**
	 * Button label
	 */
	children: PropTypes.string.isRequired,
	/**
	* Text color
	*/
	color: PropTypes.string,
	/**
	* Button color
	*/
	backgroundColor: PropTypes.string,
};
