import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { actionCreators } from '../store/Counter';

const C = props => (
  <div>
    <h1>Counter</h1>

    <p>This is a simple example of a React component with the state managed by Redux.</p>

    <p>Current count: <strong>{props.count}</strong></p>

    <button onClick={props.increment} className="btn btn-success">Increment</button>
    <button onClick={props.decrement} className="btn btn-primary" style={{marginLeft: 10}}>Decrement</button>
  </div>
);

export default connect(
  state => state.counter,
  dispatch => bindActionCreators(actionCreators, dispatch)
)(Counter);
