var parseQuery = require('./parseQuery');

function getOptions(loaderContext) {
  var query = loaderContext.query;

  if (typeof query === 'string' && query !== '') {
    return parseQuery(loaderContext.query);
  }

  if (!query || typeof query !== 'object') {
